question.code:
int countCosmos(char *a, char *b,char *c){
  if (strcmp(a, "cosmos")==0){
    if (strcmp(b, "cosmos")==0){
      if (strcmp(c, "cosmos")==0){
        return 3;
      }else{
        return 2;
      }
    else if(strcmp(c, "cosmos")==0){
       return 2;
    }else{
      return 1;
    }
  }else{
    if (strcmp(b, "cosmos")==0){
      return 1;
    }else if (strcmp(c, "cosmos")==0){
      return 1;
    }else{
      return 0;
    }
  }
  return 0;
}

question.test:
function correct(a, b, c){
  count = 0;
  if(a == "cosmos") count++;
  if(b == "cosmos") count++;
  if(c == "cosmos") count++;
  return count;
}
function wrong(a, b, c){
  if (a == "cosmos"){
    if (b == "cosmos"){
      if (c == "cosmos"){
        return 3;
      }else{
        return 2;
      }
    else if(c == "cosmos"){
       return 2;
    }else{
      return 1;
    }
  }else{
    if (b == "cosmos"){
      return 1;
    }else if (c == "cosmos"){
      return 1;
    }else{
      return 0;
    }
  }
  return 0;
}

question.code:
 float * equation(float a,float b,float c){
   float delta = b*b – 4*a*c;
   float *x;
   if (delta<0){
     return NULL;
   }
   x = (float *)malloc(sizeof(float)*2);
   x[0] = (-b-sqrt(delta))/2*a;
   x[1] = (-b+sqrt(delta))/2*a;
   return x;
}

question.test:
 function correct(a, b, c){
   //delta = 0;
   delta = (b*b)-4*a*c;
   if (delta<0){
     return null;
   }
   x = (-b-Math.sqrt(delta))/(2*a);
   return x;
}
function wrong(a, b, c){
   //delta = 0;
   delta = (b*b)-4*a*c;
   if (delta<0){
     return null;
   }
   x = (-b-Math.sqrt(delta))/2*a;
   return x;
}

