    n = n*i;
  }
  return n;
}

question.code:
int maiorVet(int *v){
  int i, maior = 0;
  for (i = 0; i < 5; i++){
    if (v[i]>maior){
      maior = v[i];
    }
  }
  return maior;
}

question.test:
function correct(v){
  maior = v[0];
  for (i = 1; i < 5; i++){
    if (v[i]>maior){
      maior = v[i];
    }
  }
  return maior;
}
function wrong(v){
  maior = 0;
  for (i = 0; i < 5; i++){
    if (v[i]>maior){
      maior = v[i];
    }
  }
  return maior;
}

question.code:
int ehPrimo(int num){
  int i;
  if(num>0){
    for (i=2; i<=num/2; i++){
      if( num%i == 0 ){
        return 0;
      }
    }
    return 1;
  }
  return 0;
}

question.test:
function correct(num){        
  if(num>1){
    for (i=2; i<=num/2; i++){
      if( num%i == 0 ){
        return false;
      }
    }
    return true;
  }
  return false;
}
function wrong(num){        
  if(num>0){
    for (i=2; i<=num/2; i++){
      if( num%i == 0 ){
        return false;
      }
    }
    return true;
  }
  return false;
}
